import React, { useState } from 'react';
import axios from 'axios';
import DataTable from 'react-data-table-component';

const columns = [
    {
        name: 'id',
        selector: (_, id) => id+1,
        sortable: false,
    },
    {
        name: 'picture',
        selector: (row, id) => {
            return <img src={row.picture.medium} alt='notloaded' height={"100px"} width={"100px"} />
        },
        sortable: true,
    },
    {
        name: 'name',
        selector: row => {
            const { name: { first, last } } = row;
            return first + " " + last;
        },
        sortable: false,
    },
    {
        name: 'email',
        selector: row => row.email,
        sortable: false,
    },
    {
        name: 'phone',
        selector: row => row.phone,
        sortable: false,
    },
    {
        name: 'address',
        selector: row => row.location.city,
        sortable: false,
    },
];

// const ExpandedComponent = ({ data }) => {
//     console.log(data);
//     return <>
//         <div>
//             <div><img src={data.picture.medium} alt="notloaded" height={"200px"} width={"200px"} /></div>
//             <div>{data.name.first}</div>
//         </div>
//     </>
// };

function RandomUserList() {

    const [randomUserList, setRandomUserList] = useState([]);
    const [isDataLoading, setIsDataLoading] = useState(false);

    const getUser = async () => {
        try {
            const { data} = await
                axios.get('https://api.coingecko.com/api/v3/coins/list');
            console.log(data);

            setRandomUserList(data);
            setIsDataLoading(false)
        } catch (error) {
            console.error(error);
        }
    }

    // useEffect(()=>{
    //     console.log(isDataLoading)
    //     if(!isDataLoading) {
    //         setIsDataLoading(true)
    //         getUser();
    //     }
    // }, [])

    return (
        <div>
            <button onClick={()=>{
                getUser()
            }}>Get Random Data</button>
             <DataTable
            columns={columns}
            data={randomUserList}
            // expandableRowsComponent={ExpandedComponent}
        />
        </div>
    )

}
export default RandomUserList;
