import axios from 'axios';
import React from 'react';
import './index.css';

const Registration = () => {

    const makeRegistration = (evt) => {
        if (evt && evt.preventDefault) {
            evt.preventDefault()
        }

        const formData = new FormData(evt.target);

        const data = {}
        for (const [key, value] of formData) {
            console.log(key, value)
            data[key] = value;
        }
        if (data) {
            if(data.password===data.confirmPassword){
            delete data.confirmPassword;
            axios.post("http://localhost:3001/registration", data);
            window.location.href = "/login"
            return true;
            }

        }



    }
    return (
        <>
            <div className="login-page">
                <div className="form">
                    <form className="login-form" onSubmit={makeRegistration}>
                        <input type="text" placeholder="name" name="name" require/>
                        <input type="text" placeholder="username" name="userName" require/>
                        <input type="text" placeholder="Email" name="email" require/>
                        <input type="password" placeholder="password" name="password" require/>
                        <input type="password" placeholder="confirm Password" name="confirmPassword" require/>
                        <div className="error"></div>
                        <button type='submit'>signup</button>
                        <p className="message">If registered? <a href="http://localhost:3000/login">Go to Login</a></p>
                    </form>
                </div>
            </div>
        </>
    )
}
export default Registration;
